import java.util.*;
public class min {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num,num1;
		Scanner scan=new Scanner(System.in);
		num=scan.nextInt();
		num1=scan.nextInt();
		if(num1<num)
		{
			System.out.println(num1);
		}
		else
			System.out.println(num);
	}

}
